import './App.css';
import { useState, useEffect } from "react";
import React from "react";
import axios from "axios";
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Input from '@mui/material/Input';

// const api = axios.create({
//   baseURL: 'localhost:5000/users'
// })

const ariaLabel = { 'aria-label': 'description' };

function App() {
  const [listOfUsers, setListOfUsers] = useState([]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/users").then((response) => {
      setListOfUsers(response.data);
    })
  }, [])

  const createUser = () => {
    axios.post("http://localhost:5000/users", {
      username,
      password,
    }).then((response) => {
      setListOfUsers([...listOfUsers, {
        username,
        password,
      }]);
    })
  }

  return (
      <div className="authenticationPage">
      {/* <h1> Hey Dude </h1>
      {console.log(listOfUsers)}
        {listOfUsers.map((user) => {
          return (
            <div>    
              /<h1>Username: {user.username}</h1>
              <h1>Password: {user.password}</h1>
            </div>
          );
        })} */}

        <h1 id="authenticationTitle">Please Log In to the Application</h1>

        <div className='inputStyling'>
          <Box
            border = {1}
            borderColor="blue"
            display="flex"
            justifyContent="center"
            alignItems="center"
            color="white"
          >
            <Input className="formStyle" type="text" error inputProps={ariaLabel} placeholder="username..." onChange={(event) => {
              setUsername(event.target.value)
            }}/>
            <Input className="formStyle" type="password" placeholder='password...' onChange={(event) => {
              setPassword(event.target.value)
            }}/>
          </Box>
        </div>

          <Button variant = "contained" color = "primary" onClick={createUser}>Create Account</Button>

      </div>


  );


}

export default App;
