import './App.css';
import { useState, useEffect } from "react";
import React from "react";
import axios from "axios";
import Button from '@material-ui/core/Button'

// const api = axios.create({
//   baseURL: 'localhost:5000/users'
// })

function App() {
  const [listOfUsers, setListOfUsers] = useState([]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/users").then((response) => {
      setListOfUsers(response.data);
    })
  }, [])

  const createUser = () => {
    axios.post("http://localhost:5000/users", {
      username,
      password,
    }).then((response) => {
      setListOfUsers([...listOfUsers, {
        username,
        password,
      }]);
    })
  }

  return (
      <div className="authenticationPage">
      {/* <h1> Hey Dude </h1>
      {console.log(listOfUsers)}
        {listOfUsers.map((user) => {
          return (
            <div>    
              /<h1>Username: {user.username}</h1>
              <h1>Password: {user.password}</h1>
            </div>
          );
        })} */}

        <h1 >Please Log In to the Application</h1>

        <div>
          <input className="formStyle" type="text" placeholder="username..." onChange={(event) => {
            setUsername(event.target.value)
          }}/>
          <input className="formStyle" type="password" placeholder='password...' onChange={(event) => {
            setPassword(event.target.value)
          }}/>
          <Button color = "primary" onClick={createUser}>Create Account</Button>
        </div>

      </div>


  );


}

export default App;
